exports.getopts = function(argv, o_single, o_pairs) {
    var opts = { known: {}, other: [] },
        arg;

    for (var i = 0; i < argv.length; i++) {
        arg = argv[i];
        if (o_single && o_single.indexOf(arg) !== -1 && (!o_pairs || o_pairs.indexOf(arg) === -1)) {
            opts.known[arg] = true;
        } else if (o_pairs && o_pairs.indexOf(arg) !== -1 && (!o_single || o_single.indexOf(arg) === -1)) {
            opts.known[arg] = argv[++i];
        } else opts.other.push(arg);
    }

    return opts;
};

exports.dateToBasicString = function(date) {
    var year = date.getFullYear(),
        month = date.getMonth(),
        day = date.getDate(),
        hours = date.getHours(),
        minutes = date.getMinutes(),
        seconds = date.getSeconds(),
        ms = date.getMilliseconds();

    function _normalize(n) { return n < 10 ? '0' + n : '' + n }

    return year + '.' +
            _normalize(month) + '.' +
            _normalize(day) + ' ' +
            _normalize(hours) + ':' +
            _normalize(minutes) + ':' +
            _normalize(seconds) + '.' +
            (ms < 10 ? '00' + ms : (ms < 100 ? '0' + ms : ms));
};
